INSERT INTO produit (code, marque, modele, prix, image, quantite, type, couleur, couleurMarque, description, rgb) VALUES
('CLA-LOG-1','Logitech','G513 Carbone',139,'logitech/G513_Carbone',15,'clavier','noir','bleu','Mélange parfait de performances, de technologies et d''une conception d''une qualité inégalée, le clavier G513 est un clavier gaming mécanique RVB équipé des switches mécaniques tactiles GX Brown plébiscités par les professionnels d''eSports du monde entier.',TRUE),
('CLA-LOG-2','Logitech','G PRO',160,'logitech/G_PRO',13,'clavier','noir','bleu','Foncez vers la victoire avec ce clavier Logitech G Pro. Au format TKL (sans pavé numérique), il est spécialement adapté pour les tournois et les parties endiablées. Ce clavier gaming est équipé des switches mécaniques tactiles avancés GX Blue pour un retour audible et tactile.',TRUE),
('CLA-LOG-3','Logitech','G915 Lightspeed Carbone',249,'logitech/G915_Lightspeed_Carbone',12,'clavier','noir','bleu','Rigoureusement conçu à partir de matériaux premium, le clavier Logitech G915 Lightspeed est doté d''un design sophistiqué, d''une robustesse à toute épreuve et de performances sans précédent. Équipé de la technologie sans fil Lightspeed, vous bénéficiez d''une réactivité extrêmement rapide.',TRUE),
('CLA-LOG-4','Logitech','G915 Tenkeyless Lightspeed Carbone',229,'logitech/G915_Tenkeyless_Lightspeed_Carbone',11,'clavier','noir','bleu','Rigoureusement conçu à partir de matériaux premium, le clavier Logitech G915 TKL Lightspeed est doté d''un design sophistiqué, d''une robustesse à toute épreuve et de performances sans précédent. Équipé de la technologie sans fil Lightspeed, vous bénéficiez d''une réactivité extrêmement rapide.',TRUE),
('CLA-LOG-5','Logitech','G815 Carbone',199,'logitech/G815_Carbone',22,'clavier','noir','bleu','Conçu pour des sessions de jeu hautes performances grâce à ses switches GL Tactile, le clavier Logitech G815 Carbone vous aidera à atteindre les sommets. Entièrement personnalisable, il est équipé de la technologie RGB Lightsync et de touches G dédiées.',TRUE),
('CLA-LOG-6','Logitech','G910 Orion Spectrum',179,'logitech/G910_Orion_Spectrum',2,'clavier','noir','bleu','Avec le Logitech G910 Orion Spectrum RGB, vous bénéficiez du clavier mécanique le plus rapide du marché. Pour cela, il s''appuie sur des switchs mécaniques Romer-G qui offrent une réactivité inédite jusqu''à + 25% et une résistance accrue autorisant plus de 70 millions de frappes !',TRUE),
('CLA-LOG-7','Logitech','Craft',189,'logitech/Craft',3,'clavier','noir','bleu','Unique en son genre, le clavier Bluetooth Logitech Craft vous offre une expérience de frappe unique et vous permettra d''exprimer pleinement votre créativité. Disposant d''une molette de contrôle contextuelle, cette dernière s''adapte à l''application que vous utilisez.',FALSE),

('CLA-COR-11','Corsair','K68',84,'Corsair/K68',4,'clavier','noir','jaune','Le clavier mécanique CORSAIR K68 RGB va vous mener jusqu''à la victoire ! Doté de switches 100 % CHERRY MX Red et de la technologie 100 % anti-ghosting avec rollover complet, ce clavier gamer assure une frappe précise et réactive avec un rétroéclairage dynamique RGB.',TRUE),
('CLA-COR-12','Corsair','K55',70,'Corsair/K55',12,'clavier','noir','jaune','Pour aligner les victoires et construire votre réputation, le clavier Corsair Gaming K55 RGB est le partenaire rêvé. Avec ses touches silencieuses et ses 6 touches macros, vous avez à votre disposition une surface de jeu idéale et performante.',TRUE),
('CLA-COR-13','Corsair','K57',109,'Corsair/K57',7,'clavier','noir','jaune','Optez pour un clavier gaming polyvalent avec le Corsair K57 pour une expérience de jeu agréable au quotidien. interrupteurs à membrane silencieux et réactifs, mode de fonctionnement hybride (filaire ou sans fil) ainsi qu''un système RGB avancé sont de la partie !',TRUE),
('CLA-COR-14','Corsair','K100',270,'Corsair/K100',1,'clavier','noir','jaune','Prenez le dessus sur vos adversaires grâce au clavier Corsair K100. Avec son design en aluminium, il offre une expérience de jeu agréable au quotidien. Il embarque notamment des switches optiques Corsair OPX ainsi qu''un rétroéclairage RGB par touche pour donner vie à votre clavier.',TRUE),
('CLA-COR-15','Corsair','K60',159,'Corsair/K60',2,'clavier','noir','jaune','A la fois stylé et performant, le Corsair K60 RGB Pro est le clavier idéal pour foncer vers la victoire. Avec son design en aluminium, il offre une expérience de jeu agréable au quotidien. Il embarque notamment des switches Cherry Viola ainsi qu''un rétroéclairage RGB par touche.',TRUE),
('CLA-COR-16','Corsair','K83',129,'Corsair/K83',3,'clavier','noir','jaune','Le clavier Corsair K83 Wireless AZERTY Noir vous permet d''accéder au meilleur du divertissement sans quitter votre canapé. Le K83 de Corsair propose un pavé tactile et un joystick pour une expérience intuitive et multi-supports grâce à sa compatibilité avec de nombreux systèmes d''exploitation.',TRUE),

('CLA-MIL-17','Millenium','Touch 2',62,'Millenium/Touch_2',7,'clavier','noir','violet','Le Touch 2 de Millenium est un clavier mécanique qui offre une frappe beaucoup plus rapide, sensible et précise afin de vous faire connaitre un plaisir de jeu incomparable. Il est doté d''interrupteurs mécaniques rouges qui peuvent supporter jusqu''à 50 millions de frappes.',TRUE),
('CLA-MIL-18','Millenium','Touch 2 Mini',59,'Millenium/Touch_2_Mini',9,'clavier','noir','violet','Le Touch 2 Mini de Millenium est un clavier mécanique compact qui offre une frappe beaucoup plus rapide, sensible et précise afin de vous faire connaitre un plaisir de jeu incomparable. Il est doté d''interrupteurs mécaniques rouges qui peuvent supporter jusqu''à 50 millions de frappes.',TRUE),

('CLA-STE-19','SteelSeries','Apex Pro TKL',239,'SteelSeries/Apex_Pro_TKL',16,'clavier','noir','orange','Véritable bond en avant, le clavier SteelSeries Apex Pro TKL vous permet de régler chaque touche en fonction de votre niveau de sensibilité préféré, que ce soit pour le gaming, le travail ou autre chose. Plus rapide et résistant que jamais, il sera l''arme ultime pour remporter des victoires.',TRUE),
('CLA-STE-21','SteelSeries','Apex Pro',259,'SteelSeries/Apex_Pro',12,'clavier','noir','orange','Véritable bond en avant, le clavier SteelSeries Apex Pro vous permet de régler chaque touche en fonction de votre niveau de sensibilité préféré, que ce soit pour le gaming, le travail ou autre chose. Plus rapide et résistant que jamais, il sera l''arme ultime pour remporter des victoires.',TRUE),
('CLA-STE-22','SteelSeries','Apex 7 TKL',179,'SteelSeries/Apex_7_TKL',5,'clavier','noir','orange','Arborant un look moderne et élégant grâce à sa structure en aluminium, le SteelSeries Apex 7 deviendra la pièce centrale de votre installation gaming. Optimisé pour le jeu, il est doté des switches SteelSeries QX2 qui pourront subir jusqu''à 50 millions de pressions.',TRUE),
('CLA-STE-23','SteelSeries','Apex 5',149,'SteelSeries/Apex_5',8,'clavier','noir','orange','Proposant des caractéristiques premium, le SteelSeries Apex 5 vous aide à prendre le dessus sur vos adversaires. Équipé de switches mécaniques hybrides, il offre des performances inégalées pour faire de vous le numéro un.',TRUE),
('CLA-STE-24','SteelSeries','Apex 3',89,'SteelSeries/Apex_3',4,'clavier','noir','orange','Profitez de performances garanties grâce au SteelSeries Apex 3. Doté d''interrupteurs à membranes à friction réduite, il vous offre une utilisation sans bruit et une durabilité accrue. De plus, avec son rétroéclairage Prism RGB sur 10 zones, donnez vie à votre clavier.',TRUE),

('CLA-RAZ-25','Razer','Blackwidow V3 TKL',119,'Razer/Blackwidow_V3_TKL',2,'clavier','noir','vert','Conçu spécifiquement pour le jeu, le Razer BlackWidow V3 TKL est un clavier compact armé de switches Razer Green afin de vous offrir une exécution précise avec une sensation tactile. Prenez ainsi facilement le dessus sur vos adversaires lors de duels.',TRUE),
('CLA-RAZ-26','Razer','Blackwidow V3 PRO',254,'Razer/Blackwidow_V3_PRO',11,'clavier','noir','vert','Conçu spécifiquement pour le jeu, le Razer BlackWidow V3 Pro dispose de 3 modes de connexion pour une polyvalence inégalée. Pouvant se connecter via RF 2,4 GHz, Bluetooth 5.0 ou en mode filaire, il vous fait vivre une incroyable expérience de jeu grâce notamment à ses switches Razer Green.',TRUE),
('CLA-RAZ-27','Razer','Blackwidow V3',159,'Razer/Blackwidow_V3',7,'clavier','noir','vert','Conçu spécifiquement pour le jeu, le Razer BlackWidow V3 est un clavier compact armé de switches Razer Green afin de vous offrir une exécution précise avec une sensation tactile. Prenez ainsi facilement le dessus sur vos adversaires lors de duels.',TRUE),
('CLA-RAZ-28','Razer','Huntsman Elite',239,'Razer/Huntsman_Elite',8,'clavier','noir','vert','Le clavier pour gamer Razer Huntsman Elite est équipé des meilleurs composants que vous pouvez trouver dans un clavier. Ne laissez plus jamais un adversaire prendre le dessus grâce aux touches opto-mécaniques Razer.',TRUE),
('CLA-RAZ-29','Razer','Huntsman',164,'Razer/Huntsman',6,'clavier','noir','vert','Ne laissez plus jamais un adversaire s''échapper avec le Razer Huntsman. Ce clavier gamer révolutionnera votre façon de jouer grâce aux touches opto-mécaniques Razer. Ces dernières disposent d''un capteur de lumière optique intégré qui actionne instantanément la commande de la touche.',TRUE),

('CLA-DUC-30','Ducky Channel','One 2 Mini',119,'Ducky_Channel/One_2_Mini',5,'clavier','noir','jaune','60% plus compact que la version classique, le Ducky Channel One 2 Mini RGB dispose de tous les avantages d''une clavier traditionnel le tout dans un format réduit. Ce clavier gamer vous promet la victoire et vous offre notamment une prise en main idéale pour cela.',TRUE),

('SOU-ALI-31','Alienware','310M',79,'Alienware/310M',7,'souris','noir','cyan','La souris de jeu Alienware 310M a été conçue pour une utilisation intensive et propose une conception robuste ainsi que d''excellente performances en alliant un capteur optique de 12 000 DPI à une autonomie maximale de 300 heures !',FALSE),
('SOU-ALI-32','Alienware','510M',99,'Alienware/510M',12,'souris','noir','cyan','La souris Alienware 510M est un redoutable instrument de précision, prêt à vous faire entrer dans une nouvelle ère de performances. Armée d''un capteur optique Alienware 16 000 DPI et de 10 boutons personnalisables à volonté, elle s''adapte à tous les jeux et à tous les styles : à vous la victoire !',FALSE),
('SOU-ALI-33','Alienware','610M',109,'Alienware/610M_Dark_Side_Of_The_Moon',15,'souris','Dark Side Of The Moon','cyan','Que vous soyez confortablement installé chez vous ou bien en déplacement, la souris Alienware 610M ne vous laissera pas tomber. En effet, elle dispose à la fois d''un mode filaire USB et d''une batterie lithium-ion haute autonomie pour une utilisation sans fil sur une durée allant jusqu''à 350 heures !',FALSE),
('SOU-ALI-34','Alienware','610M',109,'Alienware/610M_Lunar_Light',15,'souris','Lunar Light','cyan','Que vous soyez confortablement installé chez vous ou bien en déplacement, la souris Alienware 610M ne vous laissera pas tomber. En effet, elle dispose à la fois d''un mode filaire USB et d''une batterie lithium-ion haute autonomie pour une utilisation sans fil sur une durée allant jusqu''à 350 heures !',FALSE),
('SOU-ALI-35','Alienware','AW959',99,'Alienware/AW959',7,'souris','noir/blanc','cyan','Redoutable en jeu, la souris Alienware Elite AW959 offre une réactivité exceptionnelle pour les jeux de tirs ou tous les gameplay qui demandent de la précision. Équipée d''un capteur optique de 12000 dpi, la souris AW959 transmet chaque mouvement avec une précision et une réactivité optimales.',FALSE),

('SOU-AOR-36','Aorus','M2',24,'Aorus/M2',3,'souris','noir','argent','Visez les sommets avec la souris optique Aorus M2 ! Ce modèle ambidextre dispose d''une conception légère, d''un capteur optique 6200 dpi et de 7 boutons programmables. Un rétro-éclairage RGB est également disponible sur le logo pour créer un style qui vous ressemble.',TRUE),
('SOU-AOR-37','Aorus','M3',59,'Aorus/M3',2,'souris','noir','argent','La Aorus M3 est une souris filaire qui dispose d''une conception légère, d''un capteur optique 6400 dpi et de 7 boutons programmables. Un rétroéclairage RGB est également disponible sur le logo pour créer un style qui vous ressemble.',TRUE),
('SOU-AOR-38','Aorus','M4',64,'Aorus/M4',4,'souris','noir','argent','Ambidextre, la souris filaire Aorus M4 convient parfaitement aux droitiers comme aux gauchers. Embarquant un capteur optique de 6400 dpi ainsi que 8 boutons programmables, vous disposez d''une arme redoutable pour remporter de précieuses victoires. ',TRUE),
('SOU-AOR-39','Aorus','M5',79,'Aorus/M5',13,'souris','noir','argent','Vivez des parties intenses grâce à la souris pour gamer Aorus M5 et son capteur laser 16000 dpi. Disposant également de 7 boutons, d''un poids ajustable et d''un rétro-éclairage RGB, la M5 vous assure un maximum de flexibilité de personnalisation afin que la souris et votre main ne fassent qu''un.',TRUE),

('SOU-LOG-40','Logitech','G Pro HERO',79,'Logitech/G_Pro_HERO',19,'souris','noir','bleu','Profitez d''une vitesse et une précision exceptionnelles avec la souris Logitech G Pro HERO. Spécialement conçue pour l''eSport, elle intègre un capteur optique Hero de 16 000 dpi pour que chaque mouvement puisse faire mouche.',TRUE),
('SOU-LOG-41','Logitech','G Pro Wireless',129,'Logitech/G_Pro_Wireless',13,'souris','noir','bleu','Avec la Logitech G Pro Wireless Gaming Mouse, Logitech s''est associé aux joueurs d''eSports afin de concevoir la souris idéale pour les tournois. Avec une conception classique mais ergonomique, elle intègre un capteur optique Hero 16K qui offre un suivi d''une précision exceptionnelle.',TRUE),
('SOU-LOG-42','Logitech','G203 LightSync',39,'Logitech/G203_LightSync',6,'souris','noir','bleu','Avec une conception classique et des lignes simples, la Logitech G203 LightSync offre des performances conformes aux exigences des jeux vidéo en garantissant une réponse quasi instantanée aux mouvements et aux clics.',TRUE),
('SOU-LOG-43','Logitech','G305 Lightspeed Wireless',59,'Logitech/G305_Lightspeed_Wireless',7,'souris','blanc','bleu','Profitez d''une expérience sans fil incroyable grâce à la technologie sans fil Lightspeed de la Logitech G305 Lightspeed Wireless Gaming Mouse. En effet, cette dernière vous offre des performances sans fil de niveau professionnel et une fiabilité inégalée.',TRUE),
('SOU-LOG-44','Logitech','G502 Lightspeed',139,'Logitech/G502_Lightspeed',24,'souris','noir','bleu','Disposant d''une conception sans fil avec technologie Lightspeed, la Logitech G502 Lightspeed fera de vous une machine redoutable et redoutée. En effet, elle est équipée d''un capteur optique HERO de 16000 dpi pour une précision et une réactivité optimale.',TRUE),
('SOU-LOG-8','Logitech','G502',60,'logitech/G502',16,'souris','noir','bleu','description',TRUE),
('SOU-LOG-45','Logitech','G903 Lightspeed Hero Wireless',149,'Logitech/G903_Lightspeed_Hero_Wireless',32,'souris','noir','bleu','Extrêmement efficace, la Logitech G903 Lightspeed Hero Wireless Gaming Mouse offre une réactivité exceptionnelle pour les tirs de précision en compétition. Équipée du capteur optique Hero de 16000 dpi, la souris G903 transmet chaque mouvement, et ce, avec une précision et une réactivité optimales.',TRUE),
('SOU-LOG-46','Logitech','Gaming Pro X Wireless Superlight',149,'Logitech/Gaming_Pro_X__Wireless_Superlight_blanc',13,'souris','blanc','bleu','Foncez vers la victoire grâce à la souris Logitech Wireless Gaming Pro X Superlight. Nouvelle arme de prédilection des meilleurs athlètes professionnels d''eSports, elle pèse moins de 63 grammes et offre un glissement sans l moindre friction.',TRUE),
('SOU-LOG-47','Logitech','Gaming Pro X Wireless Superlight',149,'Logitech/Gaming_Pro_X__Wireless_Superlight_noir',14,'souris','noir','bleu','Foncez vers la victoire grâce à la souris Logitech Wireless Gaming Pro X Superlight. Nouvelle arme de prédilection des meilleurs athlètes professionnels d''eSports, elle pèse moins de 63 grammes et offre un glissement sans l moindre friction.',TRUE),

('SOU-RAZ-48','Razer','Naga Pro',172,'Razer/Naga_Pro',6,'souris','noir','vert','Quel que soit le jeu auquel vous jouez, la souris Razer Naga Pro apporte toute l''ergonomie et l''esthétique attendue d''un gamer ! Elle vous permet de configurer votre souris pour tout, que ce soit pour utiliser des armes ou créer des personnalisations, afin que vous écrasiez toujours la concurrence.',TRUE),
('SOU-RAZ-49','Razer','Naga Trinity',114,'Razer/Naga_Trinity',4,'souris','noir','vert','Quel que soit le jeu auquel vous jouez, la souris Razer Naga Trinity apporte toute l''ergonomie et l''esthétique attendue d''un gamer ! Elle vous permet de configurer votre souris pour tout, que ce soit pour utiliser des armes ou créer des personnalisations, afin que vous écrasiez toujours la concurrence.',TRUE),
('SOU-RAZ-50','Razer','Basilisk X HyperSpeed',59,'Razer/Basilisk_X_HyperSpeed',12,'souris','noir','vert','Quel que soit le type de jeu ou encore votre style de jeu, la Razer Basilisk X HyperSpeed répond toujours présente. Sans fil, cette souris dispose de deux modes de fonctionnement pour vous garantir une liberté incroyable.',TRUE),
('SOU-RAZ-51','Razer','Deathadder v2',84,'Razer/Deathadder_v2',7,'souris','noir','vert','Avec la souris Razer DeathAdder v2, offrez-vous une arme redoutable pour venir à bout des plus coriaces adversaires. En effet, elle intègre un capteur optique Razer Focus + de 20000 dpi afin de vous offrir une précision inégalée.',TRUE),
('SOU-RAZ-52','Razer','Deathadder v2 Pro',159,'Razer/Deathadder_v2_Pro',3,'souris','noir','vert','Venez à bout des adversaires les plus coriaces avec la souris Razer DeathAdder v2 Pro et sa double connectivité sans fil RF 2.4 GHz/Bluetooth 5.0. Offrant une totale liberté de mouvement et de contrôle, elle intègre un capteur optique Razer Focus+ de 20000 dpi et 8 boutons programmables.',TRUE),
('SOU-RAZ-53','Razer','Pro Click',114,'Razer/Pro_Click',4,'souris','blanc','vert','Conçue en collaboration avec les experts mondiaux en ergonomie bureautique Humanscale, la Razer Pro Click vous offre un confort d''utilisation incroyable. En effet, elle soutient mieux votre paume et empêche votre poignet de peser sur la surface où vous travaillez, soulageant la douleur.',TRUE),

('SOU-STE-54','SteelSeries','Aerox 3 Wireless',109,'SteelSeries/Aerox_3_Wireless',7,'souris','noir','orange','Ultralégère, la souris SteelSeries Aerox 3 Wireless permet un maniement sans effort pendant 200 heures d''utilisation. Équipée d''un capteur optique exclusif TrueMove Air de 18000 dpi et d''une double connexion sans fil Bluetooth/RF 2.4 GHz, elle offre une vitesse et une précision incroyables.',TRUE),
('SOU-STE-55','SteelSeries','Aerox 3',69,'SteelSeries/Aerox_3',12,'souris','noir','orange','Ultralégère, la souris SteelSeries Aerox 3 permet un maniement sans effort. Équipée d''un capteur optique exclusif TrueMove Core de 8500 dpi, et de patins en PTFE, elle offre une vitesse et une précision incroyable pour améliorer votre expérience de jeu.',TRUE),
('SOU-STE-56','SteelSeries','Rival 710',99,'SteelSeries/Rival_710',11,'souris','noir','orange','Accédez à un niveau de performance jamais atteint avec la SteelSeries Rival 710. Équipée du capteur optique exclusif TrueMove3, la distance parcourue sur votre tapis de souris sera la même réalisée à l''écran et sans latence ni interpolation.',TRUE),
('SOU-STE-57','SteelSeries','Sensei 310',54,'SteelSeries/Sensei_310',8,'souris','noir','orange','La souris SteelSeries Sensei 310 propose une expérience eSport totale grâce à son capteur optique 12 000 dpi TrueMove3. Performant et précis, il assure un suivi 1:1 sans latence pour une efficacité redoutable. Profitez d''un design ambidextre, de 8 boutons programmables et d''un rétro-éclairage RGB.',TRUE),
('SOU-STE-58','SteelSeries','Rival 310',54,'SteelSeries/Rival_310',9,'souris','noir','orange','Vivez des moments intenses avec la souris SteelSeries Rival 310. Digne représentante de l''eSport, elle embarque un capteur optique TrueMove3 pour un suivi 1:1 précis sans latence. Profitez d''une sensibilité jusqu''à 12 000 dpi, de 6 boutons programmables et d''un rétro-éclairage RGB sur 2 zones.',TRUE),

('SOU-MIL-59','Millenium','Optic 1 Advanced',39,'Millenium/Optic_1_Advanced',28,'souris','noir','violet','Version améliorée de la Millenium Optic 1, la Millenium Optic 1 Advanced vous permet d''augmenter votre vitesse et votre précision grâce à son capteur optique allant jusqu''à 8000 dpi. Complété d''une fréquence d''envoi 1000 Hz, bénéficiez d''une réactivité accrue.',TRUE),
('SOU-MIL-60','Millenium','Optic 1',19,'Millenium/Optic_1',16,'souris','noir','violet','Excellez dans les jeux qui requièrent une rapidité et une précision sans faille avec la Millenium Optic 1. Cette souris pour gamer est dotée d''un capteur optique allant jusqu''à 4000 dpi pour un déplacement rapide et précis en jeu',TRUE),

('SOU-COR-61','Corsair','Dark Core Pro',109,'Corsair/Dark_Core_Pro',12,'souris','noir','jaune','Proposant une connexion filaire (USB) et non filaire (Bluetooth/RF), la souris Dark Core RGB Pro de Corsair vous donne un avantage certain sur tous vos adversaires. Équipée d''un capteur optique de 18 000 dpi, elle offre des performances de gaming exceptionnelles.',TRUE),
('SOU-COR-62','Corsair','Gaming Scimitar Elite',99,'Corsair/Gaming_Scimitar_Elite',9,'souris','noir','jaune','Foncez vers la victoire avec la Corsair Gaming Scimitar RGB Elite. Cette souris dispose de 17 boutons programmables ainsi qu''un capteur optique de 18 000 dpi afin de vous permettre de terrasser vos adversaires sur les jeux MOBA et MMO.',TRUE),
('SOU-COR-63','Corsair','Glaive PRO',89,'Corsair/Glaive_PRO',12,'souris','noir','jaune','Jouez dans la cour des grands aux commandes de la souris Corsair Glaive RGB Pro et atteignez vos objectifs en toute maîtrise ! Profitez d''un confort supérieur avec grip interchangeable et d''une performance professionnelle avec un capteur optique 18 000 dpi ajustable et 7 boutons programmables.',TRUE),
('SOU-COR-64','Corsair','Ironclaw Wireless',89,'Corsair/Ironclaw_Wireless',15,'souris','noir','jaune','Réussissez votre quête et atteignez vos objectifs avec la souris Corsair Ironclaw RGB Wireless dans votre arsenal. Alliant parfaitement performances, confort et personnalisation, ce modèle vous mènera directement vers la victoire pour un futur qui s''annonce grandiose !',TRUE),
('SOU-COR-65','Corsair','Gaming Nightsword',89,'Corsair/Gaming_Nightsword',13,'souris','noir','jaune','8 boutons programmables, éclairage LED RGB  4 zones, poids ajustable de 119 gr à 141 gr et sensibilité réglable au DPI près, la Corsair Nightsword RGB vous permettra de relever tous les défis en s''adaptant à votre style de jeu.',TRUE),
('SOU-COR-66','Corsair','Gaming M65 Pro',74,'Corsair/Gaming_M65_Pro',9,'souris','noir','jaune','Aux commandes de votre souris optique Corsair Gaming M65 Pro RGB, vous allez pouvoir mettre toutes les chances de votre côté. Dotée d''un capteur optique de 12000 DPI, d''un bouton sniper et de trois zones d''éclairage personnalisables, cette souris sera un partenaire redoutable !',TRUE),
('SOU-COR-67','Corsair','Gaming M65 Elite',74,'Corsair/Gaming_M65_Elite',12,'souris','noir','jaune','Aux commandes de votre souris optique Corsair Gaming M65 RGB Elite, vous allez pouvoir mettre toutes les chances de votre côté. Dotée d''un capteur optique de 18000 DPI, d''un bouton sniper et de deux zones d''éclairage personnalisables, cette souris sera un partenaire redoutable !',TRUE);


('ENC-LOG-9','Logitech','G560',255,'logitech/G560',10,'enceinte','noir','bleu','Découvrez une nouvelle façon d''entendre vos jeux grâce au kit d''enceintes multimédia Logitech G560. En effet, le kit est doté de la technologie Lightsync afin de vous plonger au coeur de l''action.',TRUE),
('TAP-LOG-10','Logitech','G440',50,'logitech/G440',14,'tapis de souris','noir','bleu','Le tapis de souris Logitech G440 présente une surface en polymère rigide à faible friction, parfaitement adaptée au jeu haute résolution, améliorant le contrôle de la souris et permettant un placement précis du curseur.',FALSE),






